<template>
    <simple-dialog ref="dialog" class="qrcode-download-dialog" @closed="onClosed">
        <div class="wrapper flex-col vertical-center">
            <div class="qrcode-wrapper">
                <canvas class="qrcode" ref="qrcode"></canvas>
            </div>
            <span>扫描二维码下载APP</span>
            <footer></footer>
        </div>
    </simple-dialog>
</template>

<script>
    import QRCode from 'qrcode'
    import SimpleDialog from "components/common/SimpleDialog";

    export default {
        name: "QRcodeDownloadDialog",
        components: {SimpleDialog},
        data() {
            return {
                onClose: (dialog) => {}
            }
        },
        mounted() {
            this.renderQRcode();
        },
        methods: {
            open() {
                this.$refs.dialog.open();
            },
            onClosed() {
                typeof this.onClose === 'function' && this.onClose(this);
            },
            renderQRcode() {
                QRCode.toCanvas(this.$refs.qrcode, "https://app.shouduoduopay.com/sddmerser/html/download.html", {
                    margin: 0,
                    width: window.baseRem * 3
                }, error => {
                    error && console.error(error);
                })
            }
        },
    }
</script>

